use crate::{
    attestation::{Attestation, DstackAttestation},
    collateral::Collateral,
    quote::Quote,
    report_data::ReportData,
    tcbinfo::TcbInfo,
};
use alloc::string::String;
use anyhow::{Context, bail};
use backon::{BackoffBuilder, ExponentialBuilder};
use core::{future::Future, time::Duration};
use dstack_sdk::dstack_client::DstackClient;
use http::status::StatusCode;
use reqwest::multipart::Form;
use serde::Deserialize;
use tracing::error;

/// The maximum duration to wait for retrying request to Phala's endpoint.
const MAX_BACKOFF_DURATION: Duration = Duration::from_secs(60);

/// Expected HTTP [`StatusCode`] for a successful submission.
const PHALA_SUCCESS_STATUS_CODE: StatusCode = StatusCode::OK;

/// URL for submission of TDX quote. Returns collateral to be used for verification.
const PHALA_TDX_QUOTE_UPLOAD_URL: &str = "https://proof.t16z.com/api/upload";

pub struct LocalTeeAuthorityConfig;

pub struct DstackTeeAuthorityConfig {
    /// Endpoint to contact dstack service. [`None`]` defaults to `/var/run/dstack.sock`
    pub endpoint: Option<String>,
    /// URL for submission of TDX quote. Returns collateral to be used for verification.
    pub quote_upload_url: String,
}

impl Default for DstackTeeAuthorityConfig {
    fn default() -> Self {
        Self {
            endpoint: None,
            quote_upload_url: String::from(PHALA_TDX_QUOTE_UPLOAD_URL),
        }
    }
}

pub enum TeeAuthority {
    Dstack(DstackTeeAuthorityConfig),
    Local(LocalTeeAuthorityConfig),
}

impl TeeAuthority {
    pub async fn generate_attestation(
        &self,
        report_data: ReportData,
    ) -> anyhow::Result<Attestation> {
        match self {
            TeeAuthority::Local(_config) => {
                // Generate attestation using local TEE authority
                todo!("Implement local TEE attestation generation")
            }
            TeeAuthority::Dstack(config) => {
                self.generate_dstack_attestation(config, report_data).await
            }
        }
    }

    /// Generates attestation using Dstack TEE authority.
    async fn generate_dstack_attestation(
        &self,
        config: &DstackTeeAuthorityConfig,
        report_data: ReportData,
    ) -> anyhow::Result<Attestation> {
        let client = DstackClient::new(config.endpoint.as_deref());
        let tcb_info = self.get_tcb_info(&client).await;
        let tdx_quote = self.get_tdx_quote(&client, report_data).await;
        let collateral = self
            .upload_quote_for_collateral(&config.quote_upload_url, &tdx_quote)
            .await?;
        let quote = Self::parse_quote_from_hex(&tdx_quote)?;

        Ok(Attestation::Dstack(DstackAttestation::new(
            quote, collateral, tcb_info,
        )))
    }

    /// Retrieves TCB info from Dstack client.
    async fn get_tcb_info(&self, client: &DstackClient) -> TcbInfo {
        let client_info_response = get_with_backoff(|| client.info(), "dstack client info").await;
        TcbInfo::from(client_info_response.tcb_info)
    }

    /// Generates TDX quote from report data using Dstack client.
    async fn get_tdx_quote(&self, client: &DstackClient, report_data: ReportData) -> String {
        let report_data_bytes = report_data.to_bytes();
        let tdx_quote_response = get_with_backoff(
            || client.get_quote(report_data_bytes.into()),
            "dstack client tdx quote",
        )
        .await;
        tdx_quote_response.quote
    }

    /// Uploads TDX quote to Phala endpoint and retrieves collateral.
    async fn upload_quote_for_collateral(
        &self,
        quote_upload_url: &str,
        tdx_quote: &str,
    ) -> anyhow::Result<Collateral> {
        let reqwest_client = reqwest::Client::new();
        let tdx_quote = String::from(tdx_quote);

        let upload_tdx_quote = async || {
            let form = Form::new().text("hex", tdx_quote.clone());

            let response = reqwest_client
                .post(quote_upload_url)
                .multipart(form)
                .send()
                .await?;

            let status = response.status();
            if status != PHALA_SUCCESS_STATUS_CODE {
                bail!(
                    "Got unexpected HTTP status code: response from phala endpoint: {:?}, expected: {:?}",
                    status,
                    PHALA_SUCCESS_STATUS_CODE
                );
            }

            response
                .json::<UploadResponse>()
                .await
                .context("Failed to deserialize response from Phala.")
        };

        let upload_response = get_with_backoff(upload_tdx_quote, "upload tdx quote").await;

        Collateral::try_from(upload_response.quote_collateral)
            .map_err(|e| anyhow::anyhow!("Failed to parse collateral: {}", e))
    }

    /// Parses a hex-encoded TDX quote into a Quote object.
    fn parse_quote_from_hex(tdx_quote_hex: &str) -> anyhow::Result<Quote> {
        let quote_bytes = hex::decode(tdx_quote_hex)?;
        let dcap_quote = dcap_qvl::quote::Quote::parse(quote_bytes.as_slice())
            .map_err(|e| anyhow::anyhow!("Failed to parse quote: {:?}", e))?;
        Ok(Quote::from(dcap_quote))
    }
}

#[derive(Deserialize)]
struct UploadResponse {
    quote_collateral: serde_json::Value,
    #[serde(rename = "checksum")]
    _checksum: String,
}

async fn get_with_backoff<Operation, OperationFuture, Value, Error>(
    operation: Operation,
    description: &str,
) -> Value
where
    Error: core::fmt::Debug,
    Operation: Fn() -> OperationFuture,
    OperationFuture: Future<Output = Result<Value, Error>>,
{
    let mut backoff = ExponentialBuilder::default()
        .with_max_delay(MAX_BACKOFF_DURATION)
        .without_max_times()
        .with_jitter()
        .build();

    // Loop until we have a response.
    loop {
        match operation().await {
            Err(err) => {
                let duration = backoff.next().unwrap_or(MAX_BACKOFF_DURATION);
                error!(?err, "{description} failed. retrying in: {:?}", duration);
                continue;
            }
            Ok(response) => break response,
        }
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_dstack_tee_authority_config_default() {
        let config = DstackTeeAuthorityConfig::default();
        assert_eq!(config.endpoint, None);
        assert_eq!(config.quote_upload_url, PHALA_TDX_QUOTE_UPLOAD_URL);
    }

    #[test]
    fn test_parse_quote_from_hex_valid() {
        // Use the actual valid quote hex from the chain-signatures contract tests
        let valid_quote_hex = "";
        let result = TeeAuthority::parse_quote_from_hex(valid_quote_hex);

        assert!(
            result.is_ok(),
            "Should successfully parse valid TDX quote hex"
        );
        let quote = result.unwrap();

        // Verify the quote was parsed correctly by accessing the TD report
        // The quote should have a TD report with RTMR3 measurements
        let td_report = quote.report.as_td10().expect("Should be a TD 1.0 report");

        // Verify that RTMR3 is not all zeros (which would indicate an issue)
        let rtmr3_all_zeros = [0u8; 48];
        assert_ne!(
            td_report.rt_mr3, rtmr3_all_zeros,
            "RTMR3 should not be all zeros"
        );

        // The actual RTMR3 value from this specific quote
        let expected_rtmr3 = [
            0x56, 0x1c, 0x1b, 0x02, 0x35, 0x1c, 0xd6, 0xf7, 0xc8, 0x03, 0xdd, 0x36, 0xbc, 0x95,
            0xba, 0x25, 0x46, 0x3a, 0xa0, 0x25, 0xce, 0x77, 0x61, 0x15, 0x62, 0x60, 0xc9, 0x13,
            0x1a, 0x5d, 0x7c, 0x03, 0xae, 0xcc, 0xc1, 0x0e, 0x12, 0x16, 0x0e, 0xc3, 0x20, 0x5b,
            0xb2, 0x87, 0x6a, 0x20, 0x3a, 0x7f,
        ];
        assert_eq!(
            td_report.rt_mr3, expected_rtmr3,
            "RTMR3 should match expected value"
        );
    }

    #[test]
    fn test_parse_quote_from_hex_invalid() {
        let invalid_hex = "invalid_hex_string";
        let result = TeeAuthority::parse_quote_from_hex(invalid_hex);

        assert!(result.is_err(), "Should fail to parse invalid hex");
    }

    #[test]
    fn test_parse_quote_from_hex_empty() {
        let empty_hex = "";
        let result = TeeAuthority::parse_quote_from_hex(empty_hex);

        assert!(result.is_err(), "Should fail to parse empty hex");
    }

    #[test]
    fn test_parse_quote_from_hex_malformed_quote() {
        let malformed_hex = "deadbeef"; // Valid hex but not a valid quote
        let result = TeeAuthority::parse_quote_from_hex(malformed_hex);

        assert!(result.is_err(), "Should fail to parse malformed quote");
    }
}
